from django.shortcuts import render
from django.http import HttpResponse

class Pais:
    def __init__(self, posicao, nome_pais, ouro, prata, bronze, img_link):
        self.posicao = posicao
        self.nome_pais = nome_pais
        self.ouro = ouro
        self.prata = prata
        self.bronze = bronze
        self.img_link = img_link
        self.atletas = []
            
eua = Pais('1', 'Estados Unidos','27', '35', '33', 'data:image/png;base64,' )
china = Pais('2', 'China', '29', '25', '19',  'data:image/png;base64,')
australia = Pais('3', 'Austrália', '18', '14', '13', 'data:image/png;base64,')
brasil = Pais('19', 'Brasil', '2', '5', '8', 'https://ssl.gstatic.com/onebox/media/sports/logos/zKLzoJVYz0bb6oAnPUdwWQ_48x48.png')
lista_paises = [eua, china, australia, brasil]


class Atletas:
    def __init__(self, atleta, modalidade, medalha):

        self.atleta = atleta
        self.modalidade = modalidade
        self.medalha = medalha
    
    def __str__(self):
        return self.atleta
    
def procura_instancia_pais(pais_escolhido):
    for pais in lista_paises:
        if pais_escolhido == pais.nome_pais:
            return pais

brasil.atletas.append(Atletas('Rebecca', 'Ginástica', 'Ouro'))

def index(request):
    return render(request, 'medalhas/tabela_paises.html', {
    "paises": lista_paises
    })

def atletas_medalhas(request, pais_medalhas):
    pais_escolhido = procura_instancia_pais(pais_medalhas)
    return HttpResponse(f"{pais_escolhido.atletas[0]}")